/**
* @Path :起到定位作用，如 http://localhost:8080/OSPServer/rest/save/saveform，这个链接，通过save定位到类：SaveFormModel，通过saveform定位到方法：saveFormData()；
* @POST :HTTP方法，还有@GET
* @Produces :指定将要返回给client端的数据标识类型(MIME)
* @Consumes :代表的是一个资源可以接受的数据标识类型(MIME)
* 注：类要继承 BaseRESTfulService以便于获取RESTful服务的基类（HttpServletRequest、HttpServletResponse、ServletConfig、ServletContext、UriInfo、HttpHeaders）
*/
package osp.test;

import javax.ws.rs.Consumes;
import javax.ws.rs.POST;
import javax.ws.rs.Path;
import javax.ws.rs.Produces;
import javax.ws.rs.core.MediaType;

import org.slf4j.Logger;
import org.slf4j.LoggerFactory;

import com.efounder.api.jersey.model.RESTResponseObject;
import com.efounder.api.jersey.service.BaseRESTfulService;
import com.efounder.eai.EAI;
import com.efounder.eai.data.JResponseObject;
import com.efounder.form.SuperJsonModel;

@Path("/save")
public class SaveFormModel extends BaseRESTfulService{
    private static final Logger logger = LoggerFactory.getLogger(SaveFormModel.class);

    @POST
    @Path("/saveform")
    @Produces(MediaType.APPLICATION_JSON)
    @Consumes(MediaType.APPLICATION_JSON)
    public RESTResponseObject saveFormData(RESTFormDataModel formDataModel) throws Exception {
        // 获取静态参数并放入参数对象
        formDataModel.SetValueByParamName("DCT_ID", "SYS_OBJECTS");

        StubObject SO = RESTfulStaticParamUtil.getSOById("saveform");
        if (SO != null) {
            formDataModel.SetValueByParamName("DBNO", SO.getString("DBNO", ""));
            formDataModel.SetValueByParamName("DataBaseName", SO.getString("DataBaseName", ""));
        }

        JParamObject po = formDataModel.getParamObject();
        System.out.println("paramObject = " + JSON.toJSONString(po));

        EFFormDataModel formData = formDataModel.getFormData();
